mapper.update:
update notice
set title=#{title}, content=#{content}
where bno=#{bno}

mapper.select:
	
	select bno, title, content, writer, regdate, viewcnt
		from (select @rownum:=@rownum+1 as rownum, b.* 
			from (select @rownum:=0) as tmp, notice as b 

		 
	  
	    order by bno desc
	   ) as boardList
	
	
		where rownum > (#{pageNum}-1)*#{amount} 
			and rownum <= #{pageNum}*#{amount}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM ARTICLE 
					ORDER BY SEQ DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
update community
set title=#{title}, category=#{category}, content=#{content}
where bno=#{bno}

mapper.select:
	
	select bno, category, title, content, writer, regdate, viewcnt
		from (select @rownum:=@rownum+1 as rownum, b.* 
			from (select @rownum:=0) as tmp, community as b 

		 
	  
	    order by bno desc
	   ) as boardList
	
	
		where rownum > (#{pageNum}-1)*#{amount} 
			and rownum <= #{pageNum}*#{amount};
