mapper.select:
SELECT COUNT(likeno) as cnt
	FROM liketo
		WHERE bno=#{bno} AND userid=#{userid}

mapper.update:
UPDATE liketo
	SET likecheck = likecheck + 1 
		WHERE userid=#{userid} AND bno=#{bno}

mapper.select:
 SELECT likeno, boardno, mno, likecheck 
		FROM liketo
 		WHERE bno=#{bno} AND userid=#{userid}
