mapper.update:
update article
set title=#{title}, content=#{content}, regdate=now() 
where bno=#{bno}

mapper.select:
	
	select bno, category, title, content, writer, regdate, viewcnt
		from (select @rownum:=@rownum+1 as rownum, b.* 
			from (select @rownum:=0) as tmp, article as b 

		 
	  
	    order by bno desc
	   ) as boardList
	
	
		where rownum > (#{pageNum}-1)*#{amount} 
			and rownum <= #{pageNum}*#{amount};
